-- FUNCTION: public.clone_asset(integer, text, text, integer, integer, boolean, boolean, boolean)

-- DROP FUNCTION IF EXISTS public.clone_asset(integer, text, text, integer, integer, boolean, boolean, boolean);

CREATE OR REPLACE FUNCTION public.clone_asset(
old_asset_id integer,
new_asset_name text,
new_asset_display_name text,
new_parent_asset_id integer,
user_id integer,
clone_analytics boolean DEFAULT false,
clone_alarm_settings boolean DEFAULT false,
clone_alarm_receipents boolean DEFAULT false)
    RETURNS boolean
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$


  declare 
  new_asset_id int default 0;
  new_asset_detail_id int default 0;
  new_primary_asset_detail_id  int default 0;
  new_alarm_setting_id int default 0;
  current_alarm_setting_id int default 0;
  alarm_notification_recipients_id int default 0;
  new_all_jsons_id int default 0;
  new_analytic_parameters_id int default 0;
  new_asset_analytics_id int default 0;
  new_asset_data_id  int default 0;
  new_asset_datatype_id int default 0;
  new_user_asset_map_id int default 0;
  rec record;
  new_rec record;
  alarm_setting_rec record;
  return_msg text;
  return_value boolean default true;
  v_state text;
  v_msg text;
  v_detail text;
  v_hint text;
  v_context text;

begin
return_msg  = 'script execution started';
/*----insert into assets ---*/
begin
insert into assets (asset_name, author, status_id, asset_type_id, display_name, parent_asset_id, order_no,json_data) 
select $2, author, status_id, asset_type_id, $3, $4, order_no, json_data from public.assets where id = $1
returning id into new_asset_id;
exception when others then     
    get stacked diagnostics  v_state = returned_sqlstate, v_msg = message_text, v_detail = pg_exception_detail, v_hint = pg_exception_hint, v_context = pg_exception_context;
return_msg  = 'script execution failed while inserting into assets table';
raise notice e' got exception: state: %  message: %  detail: %  hint: %  context: %',  v_state, v_msg, v_detail, v_hint, v_context;   
return_msg  = return_msg||':-'||v_state||':-'||v_msg||':-'||v_detail||':-'||v_hint||':-'||v_context;
return_value = false;
end;
