-- View: public.mat_view_asset_details_pagination

DROP MATERIALIZED VIEW IF EXISTS public.mat_view_asset_details_pagination;

CREATE MATERIALIZED VIEW IF NOT EXISTS public.mat_view_asset_details_pagination
TABLESPACE pg_default
AS
 SELECT a.sid,
    a.id,
    a.subsystem,
    a.tag_name,
    a.status_id,
    a.status,
    a.displayname,
    a.unit_id,
    a.unitname,
    a.tag_type,
    a.monitoring_type,
    a.voltage_side,
    a.voltage_phase,
    a.asset_type_id,
    a.asset_type_name,
    a.asset_id,
    a.asset_name,
    a.configuration,
    a.monitoringcatg,
    a.offlinetesttype,
    a.isenum,
    a.isenumtype,
    a.showinvitals,
    a.showintrends,
    a.showtrendsteps
   FROM ( SELECT row_number() OVER () AS sid,
            ad.id,
            atd.subsystem,
            atd.tag_name,
            ad.status_id,
                CASE
                    WHEN ad.status_id = 1 THEN 'Enabled'::text
                    WHEN ad.status_id = 2 THEN 'Disabled'::text
                    ELSE ''::text
                END AS status,
                CASE
                    WHEN ad.status_id = 1 THEN ad.display_name
                    ELSE atd.display_name
                END AS displayname,
            atd.unit_id,
            u.symbol AS unitname,
            atd.tag_type,
                CASE
                    WHEN ad.status_id = 1 THEN ad.monitoring_type
                    ELSE atd.monitoring_type
                END AS monitoring_type,
                CASE
                    WHEN ad.status_id = 1 THEN ad.voltage_side
                    ELSE atd.voltage_side
                END AS voltage_side,
                CASE
                    WHEN ad.status_id = 1 THEN ad.voltage_phase
                    ELSE atd.voltage_phase
                END AS voltage_phase,
            a_1.asset_type_id,
            at.asset_type_name,
            a_1.id AS asset_id,
            a_1.asset_name,
            atd.configuration,
                CASE
                    WHEN ad.status_id = 1 THEN ad.monitoring_catg
                    ELSE atd.monitoring_catg
                END AS monitoringcatg,
            atd.offlinetesttype,
                CASE
                    WHEN ad.status_id = 1 THEN ad.is_enum
                    ELSE atd.is_enum
                END AS isenum,
                CASE
                    WHEN ad.status_id = 1 THEN ad.enum_type
                    ELSE atd.enum_type
                END AS isenumtype,
                CASE
                    WHEN ad.status_id = 1 THEN (ad.jsonobject ->> 'showInVitals'::text)::boolean
                    ELSE (atd.jsonobject ->> 'showInVitals'::text)::boolean
                END AS showinvitals,
                CASE
                    WHEN ad.status_id = 1 THEN (ad.jsonobject ->> 'showInTrends'::text)::boolean
                    ELSE (atd.jsonobject ->> 'showInTrends'::text)::boolean
                END AS showintrends,
                CASE
                    WHEN ad.status_id = 1 THEN (ad.jsonobject ->> 'showTrendSteps'::text)::boolean
                    ELSE (atd.jsonobject ->> 'showTrendSteps'::text)::boolean
                END AS showtrendsteps
           FROM asset_type_details atd
             JOIN asset_types at ON at.id = atd.asset_type_id AND atd.status_id = 1 AND at.status_id = 1
             JOIN assets a_1 ON a_1.asset_type_id = at.id AND a_1.status_id = 1 AND atd.asset_type_id = a_1.asset_type_id
             LEFT JOIN asset_details ad ON a_1.id = ad.asset_id AND ad.status_id = 1 AND atd.tag_type::text = ad.tag_type::text AND atd.tag_name::text = ad.tag_name::text AND atd.configuration::text = ad.configuration::text
             LEFT JOIN units u ON u.id = atd.unit_id AND u.status_id = 1) a
WITH DATA;

