create or replace function reset_sequence_ids() returns void
LANGUAGE plpgsql as
$function$
declare
dynamic_statement varchar;
begin
WITH my_qry AS (
select 'select'||' '||replace(c.column_default,'nextval(','setval((')||',greatest(coalesce(max('||column_name||'),0)+1,('||c.column_default||'-1))) from '||c.table_name as dyn_stmt FROM information_schema.columns c WHERE table_schema = 'public' and column_default LIKE 'nextval%' order by c.table_name
) SELECT
string_agg(dyn_stmt,';'||chr(10)) into dynamic_statement
FROM my_qry;
execute (dynamic_statement);
end
$function$
;
--end of create or replace the function
