    ids BIGINT[]
)
RETURNS TABLE(
    gateway_time TIMESTAMP WITHOUT TIME ZONE,
    asset_detail_id INTEGER,
    value_double DOUBLE PRECISION,
    roc DOUBLE PRECISION,
    is_valid BOOLEAN,
    tag_name VARCHAR
) 
LANGUAGE plpgsql
COST 100
VOLATILE PARALLEL SAFE 
ROWS 1000
AS $BODY$
BEGIN
    RETURN QUERY
    WITH closest_before_start AS (
        -- Find the latest data before `start_time`
        SELECT DISTINCT ON (cth.asset_detail_id)
            unnested.gateway_time AS gateway_time,
            cth.asset_detail_id AS asset_id,
            unnested.val_double AS val_double,
            unnested.rate_of_change AS rate_of_change
        FROM public.realtime_data cth,
             LATERAL unnest(cth.gateway_time, cth.value_double, cth.roc) AS unnested(gateway_time, val_double, rate_of_change)
        WHERE cth.asset_detail_id = ANY(ids)
          AND unnested.gateway_time <= start_time
        ORDER BY cth.asset_detail_id, gateway_time DESC
    ),
    extended_to_start AS (
        -- Extend the data closest to `start_time` to exactly `start_time`
        SELECT
            start_time AS gateway_time,
            asset_id AS asset_detail_id,
            val_double AS value_double,
            rate_of_change AS roc,
            false AS is_valid
        FROM closest_before_start
    ),
    closest_before_end AS (
        -- Find the latest data before `end_time`
        SELECT DISTINCT ON (cth.asset_detail_id)
            unnested.gateway_time AS gateway_time,
            cth.asset_detail_id AS asset_id,
            unnested.val_double AS val_double,
            unnested.rate_of_change AS rate_of_change
        FROM public.realtime_data cth,
             LATERAL unnest(cth.gateway_time, cth.value_double, cth.roc) AS unnested(gateway_time, val_double, rate_of_change)
        WHERE cth.asset_detail_id = ANY(ids)
          AND unnested.gateway_time <= end_time
        ORDER BY cth.asset_detail_id, gateway_time DESC
    ),
    extended_to_end AS (
        -- Extend the data closest to `end_time` to exactly `end_time`
        SELECT
            end_time AS gateway_time,
            asset_id AS asset_detail_id,
            val_double AS value_double,
            rate_of_change AS roc,
            false AS is_valid
        FROM closest_before_end
    ),
    valid_data_in_range AS (
        -- Fetch valid data between `start_time` and `end_time`
        SELECT
            unnested.gateway_time AS gateway_time,
            cth.asset_detail_id AS asset_id,
            unnested.val_double AS val_double,
            unnested.rate_of_change AS rate_of_change,
            true AS is_valid
        FROM public.realtime_data cth,
             LATERAL unnest(cth.gateway_time, cth.value_double, cth.roc) AS unnested(gateway_time, val_double, rate_of_change)
        WHERE cth.asset_detail_id = ANY(ids)
          AND unnested.gateway_time BETWEEN start_time AND end_time
    )
    -- Combine all data: extended to start, valid range, extended to end
    SELECT
        cd.gateway_time,
        cd.asset_detail_id,
        cd.value_double,
        cd.roc,
        cd.is_valid,
        ad.tag_name
    FROM (
        SELECT * FROM extended_to_start
        UNION ALL
        SELECT * FROM valid_data_in_range
        UNION ALL
        SELECT * FROM extended_to_end
    ) cd
    JOIN asset_details ad ON ad.id = cd.asset_detail_id
    ORDER BY cd.gateway_time, ad.tag_name;
END;
$BODY$;

