CREATE OR REPLACE FUNCTION public.clone_asset(
old_asset_id integer,
new_asset_name text,
new_asset_display_name text,
new_parent_asset_id integer,
user_id integer,
clone_analytics boolean DEFAULT false,
clone_alarm_settings boolean DEFAULT false,
clone_alarm_receipents boolean DEFAULT false)
    RETURNS boolean
    LANGUAGE 'plpgsql'
    COST 100
    VOLATILE PARALLEL UNSAFE
AS $BODY$


  declare 
  new_asset_id int default 0;
  new_primary_asset_detail_id  int default 0;
  new_alarm_setting_id int default 0;
  current_alarm_setting_id int default 0;
  alarm_notification_recipients_id int default 0;
  new_all_jsons_id int default 0;
  new_analytic_parameters_id int default 0;
  new_asset_analytics_id int default 0;
  new_asset_data_id  int default 0;
  new_asset_datatype_id int default 0;
  new_user_asset_map_id int default 0;
  rec record;
  new_rec record;
  alarm_setting_rec record;
  return_msg text;
  return_value boolean default true;
  v_state text;
  v_msg text;
  v_detail text;
  v_hint text;
  v_context text;

begin
return_msg  = 'script execution started';

/* create temp table to hold old and new asset detail id mapping*/
if not exists (select 1 from pg_tables where lower(tablename) = 'asset_detail_id_clone_mapping') then
 -- create the temporary table if it doesn't exist
create temp table asset_detail_id_clone_mapping (
    id serial,
    old_asset_detail_id INT,
    new_asset_detail_id INT,
    created_time timestamp default current_timestamp
);
-- add unique constraint to the temporary table
alter table asset_detail_id_clone_mapping add constraint ui_asset_detail_id_clone_mapping unique (old_asset_detail_id);
end if; -- create temp table

/*----insert into assets ---*/
begin
insert into assets (asset_name, author, status_id, asset_type_id, display_name, parent_asset_id, order_no,json_data) 
select $2, author, status_id, asset_type_id, $3, $4, order_no, json_data from public.assets where id = $1
returning id into new_asset_id;
exception when others then     
    get stacked diagnostics  v_state = returned_sqlstate, v_msg = message_text, v_detail = pg_exception_detail, v_hint = pg_exception_hint, v_context = pg_exception_context;
return_msg  = 'script execution failed while inserting into assets table';
raise notice e' got exception while inserting into assets table: state: %  message: %  detail: %  hint: %  context: %',  v_state, v_msg, v_detail, v_hint, v_context;   
return_msg  = return_msg||':-'||v_state||':-'||v_msg||':-'||v_detail||':-'||v_hint||':-'||v_context;
return_value = false;
end;



/*-- insert into asset_detail_id_mapping table to map old and new asset_detail ID's -- */

begin

insert into asset_detail_id_clone_mapping (old_asset_detail_id, new_asset_detail_id)
select id,nextval('asset_details_id_seq'::regclass) from asset_details where asset_id = $1 order by id;
exception when others then     
    get stacked diagnostics  v_state = returned_sqlstate, v_msg = message_text, v_detail = pg_exception_detail, v_hint = pg_exception_hint, v_context = pg_exception_context;
return_msg  = 'script execution failed while inserting into asset_detail_id_clone_mapping temp table';
raise notice e' got exception while inserting into asset_detail_id_clone_mapping temp table: state: %  message: %  detail: %  hint: %  context: %',  v_state, v_msg, v_detail, v_hint, v_context;   
return_msg  = return_msg||':-'||v_state||':-'||v_msg||':-'||v_detail||':-'||v_hint||':-'||v_context;
return_value = false;
end;


/*-- insert into asset_details table -- */

begin

insert into public.asset_details
(id,author, subsystem, tag_name, display_name, unit_id, status_id, asset_id, tag_type, monitoring_type, formula_text, voltage_side, voltage_phase, "configuration", frequency, monitoring_catg, offlinetesttype, is_enum, enum_type, jsonobject)
select new_asset_detail_id,ad.author, ad.subsystem, ad.tag_name, ad.display_name, ad.unit_id, ad.status_id, @new_asset_id, ad.tag_type, ad.monitoring_type, ad.formula_text, ad.voltage_side, ad.voltage_phase, ad."configuration", ad.frequency, ad.monitoring_catg, ad.offlinetesttype, ad.is_enum, ad.enum_type, ad.jsonobject 
from assets a, asset_details ad ,asset_detail_id_clone_mapping adcmap
where ad.id = adcmap.old_asset_detail_id and a.id = ad.asset_id and a.id = $1 order by ad.id;
exception when others then     
     get stacked diagnostics  v_state = returned_sqlstate, v_msg = message_text, v_detail = pg_exception_detail, v_hint = pg_exception_hint, v_context = pg_exception_context;
 return_msg  = 'script execution failed while inserting into asset_details table';
raise notice e' got exception while inserting into asset_details table: state: %  message: %  detail: %  hint: %  context: %',  v_state, v_msg, v_detail, v_hint, v_context;   
return_msg  = return_msg||':-'||v_state||':-'||v_msg||':-'||v_detail||':-'||v_hint||':-'||v_context;
return_value = false;
end;

/*----insert into  public.all_jsons ---*/

begin
insert into public.all_jsons
(asset_id, asset_type_name, json_type, subsystem, "json")
select @new_asset_id,asset_type_name,json_type,subsystem,"json" from all_jsons aj where aj.asset_id = $1;
exception when others then     
    get stacked diagnostics  v_state = returned_sqlstate, v_msg = message_text, v_detail = pg_exception_detail, v_hint = pg_exception_hint, v_context = pg_exception_context;
return_msg  = 'script execution failed while inserting into all_jsons table';
