/*----insert into alarm_settings when alarm settings flag is true---*/
begin
insert into public.alarm_settings
(author, status_id, asset_detail_id, direction, display_name, alarm_threshold, alarm_hysteresis, latch, reccomendation, notification_type, primary_asset_detail_id, warning_threshold, warning_hysteresis, alert_category, send_notification, template_type,dynamic_alarm_threshold, notify_delay, notify_delay_units)
