alarm_setting_rec.primary_asset_id, als.warning_threshold, als.warning_hysteresis, als.alert_category, als.send_notification, als.template_type,als.dynamic_alarm_threshold, als.notify_delay, als.notify_delay_units 
from  alarm_settings als where als.id = alarm_setting_rec.id returning id into new_alarm_setting_id;
exception when others then     
    get stacked diagnostics  v_state = returned_sqlstate, v_msg = message_text, v_detail = pg_exception_detail, v_hint = pg_exception_hint, v_context = pg_exception_context;
return_msg  = 'script execution failed while inserting into alarm_settings table';
raise notice e' got exception while inserting into alarm_settings table: state: %  message: %  detail: %  hint: %  context: %',  v_state, v_msg, v_detail, v_hint, v_context;   
return_msg  = return_msg||':-'||v_state||':-'||v_msg||':-'||v_detail||':-'||v_hint||':-'||v_context;
return_value = false;
end;

/*----insert into alarm_notification_recipients when alarm settings flag is true and alarm receipents flag is also true---*/
if (clone_alarm_receipents) then
begin
insert into public.alarm_notification_recipients
(alarm_setting_id, email_recipients, sms_recipients, external_email_recipients)
select @new_alarm_setting_id,email_recipients, sms_recipients, external_email_recipients 
from alarm_notification_recipients where alarm_setting_id = alarm_setting_rec.id 
returning id into alarm_notification_recipients_id;
exception when others then     
    get stacked diagnostics  v_state = returned_sqlstate, v_msg = message_text, v_detail = pg_exception_detail, v_hint = pg_exception_hint, v_context = pg_exception_context;
return_msg  = 'script execution failed while inserting into alarm_notification_recipients table';
raise notice e' got exception while inserting into alarm_notification_recipients table: state: %  message: %  detail: %  hint: %  context: %',  v_state, v_msg, v_detail, v_hint, v_context;   
return_msg  = return_msg||':-'||v_state||':-'||v_msg||':-'||v_detail||':-'||v_hint||':-'||v_context;
return_value = false;
end;
end if; -- clone_alarm_receipents

end loop; -- alarm settings

end if; -- clone_alarm_settings flag

